import express from 'express';
import {signup, signin } from '../controler/login';
import { check_auth } from '../midelwear/chech_auth';
import {add, updateInfor, updatePass, lockUnlUser, deleteUser, setRole, getListUser, getListAdmin, getListLock} from '../controler/users'

const router = express.Router();

router.post('/signup', signup);
router.post('/signin', signin );
router.post('/add',  add );
router.post('/updateInfor', updateInfor);
router.post('/updatePass', updatePass);
router.post('/lockUnlUser', lockUnlUser);
router.delete('/deleteUser', deleteUser);
router.post('/setRole', setRole);
router.get('/getListUser', getListUser);
router.get('/getListAdmin', getListAdmin);
router.get('/getListLock', getListLock )

export const userRt = router;

