import mongoose from 'mongoose';
import { userdb } from '../models/users'
import { hash } from '../validate/hash'
import { checkName, checkPass } from '../validate/check'
import { has } from 'lodash';



export const add = async (req, res, next) => {
    const { id, password, name } = req.body;
    const data = await userdb.find({ id: id });
    if (data[0]) {
        res.json('tai khoan da ton tai');
    } else {
        try {
            checkName(name)
            checkPass(password)
            const hashPassword = await hash(password);
            const newUser = {
                _id: new mongoose.Types.ObjectId(),
                passWord: hashPassword,
                ...req.body
            };
            await userdb.create(newUser)
            res.json('thanh cong')
        } catch (err) {
            res.json(err + '')
        }
    }
}
export const updateInfor = async (req, res, next) => {
    const { id, password, name } = req.body;
    const data = await userdb.find({ id: id });
    if (data[0]) {
        try {
            if (name) {
                checkName(name)
            }
            const udUser = {
                passWord: data[0].passWord,
                ...req.body
            };
            await userdb.where({ id: id }).update(udUser)
            res.json('thanh cong')
        } catch (err) {
            res.json(err + '')
        }
    } else {
        res.json('tai khoan khong ton tai')
    }
}
export const updatePass = async (req, res, next) => {
    const { id, password } = req.body;
    const data = await userdb.find({ id: id });
    if (data[0]) {
        try {
            checkPass(password)
            const hashPassword = await hash(password);
            await userdb.where({ id: id }).update({ passWord: hashPassword });
            res.json('thanh cong!')
        } catch (err) {
            res.json(err + '')
        }
    } else {
        res.json('tai khoan khong ton tai')
    }
}
export const lockUnlUser = async (req, res, next) => {
    const { id, status } = req.body;
    const data = await userdb.find({ id: id });
    if (data[0]) {
        await userdb.where({ id: id }).update({ status: status })
        res.json('thanh cong')
    } else {
        res.json('tai khoan khong ton tai')
    }
}
export const deleteUser = async (req, res, next) => {
    const { id } = req.body;
    const data = await userdb.find({ id: id })
    if (data[0]) {
        await userdb.remove({ id: id });
        res.json('thanh cong')
    } else {
        res.json('tai khoan khong ton tai')
    }
}
export const setRole = async (req, res, next) => {
    const { id, role } = req.body;
    const data = await userdb.find({ id: id });
    if (data[0]) {
        await userdb.where({ id: id }).update({ role: role })
        res.json('thanh cong')
    } else {
        res.json('tai khoan khon ton tai')
    }
}
export const getListUser = async (req, res) => {
    const data = await userdb.find({ role: 'user', status: 'active' }, { passWord: 0, _id: 0 })
    res.json(data)
}
export const getListAdmin = async (req, res) => {
    const data = await userdb.find({ role: 'admin', status: 'active' }, { passWord: 0, _id: 0 })
    res.json(data)
}
export const getListLock = async (req, res) => {
    const data = await userdb.find({ status: 'none' }, { passWord: 0, _id: 0 })
    res.json(data)
}

